@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Acanvas-lms
@description Soothing pastel theme for Canvas LMS
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@-moz-document domain("instructure.com") {
  :root {
    @media (prefers-color-scheme: light) {
      #catppuccin(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
      #catppuccin(@darkFlavor);
    }
  }

  #catppuccin(@flavor) {
    @rosewater: @catppuccin[@@flavor][@rosewater];
    @flamingo: @catppuccin[@@flavor][@flamingo];
    @pink: @catppuccin[@@flavor][@pink];
    @mauve: @catppuccin[@@flavor][@mauve];
    @red: @catppuccin[@@flavor][@red];
    @maroon: @catppuccin[@@flavor][@maroon];
    @peach: @catppuccin[@@flavor][@peach];
    @yellow: @catppuccin[@@flavor][@yellow];
    @green: @catppuccin[@@flavor][@green];
    @teal: @catppuccin[@@flavor][@teal];
    @sky: @catppuccin[@@flavor][@sky];
    @sapphire: @catppuccin[@@flavor][@sapphire];
    @blue: @catppuccin[@@flavor][@blue];
    @lavender: @catppuccin[@@flavor][@lavender];
    @text: @catppuccin[@@flavor][@text];
    @subtext1: @catppuccin[@@flavor][@subtext1];
    @subtext0: @catppuccin[@@flavor][@subtext0];
    @overlay2: @catppuccin[@@flavor][@overlay2];
    @overlay1: @catppuccin[@@flavor][@overlay1];
    @overlay0: @catppuccin[@@flavor][@overlay0];
    @surface2: @catppuccin[@@flavor][@surface2];
    @surface1: @catppuccin[@@flavor][@surface1];
    @surface0: @catppuccin[@@flavor][@surface0];
    @base: @catppuccin[@@flavor][@base];
    @mantle: @catppuccin[@@flavor][@mantle];
    @crust: @catppuccin[@@flavor][@crust];
    @accent: @catppuccin[@@flavor][@@accentColor];

    color-scheme: if(@flavor = latte, light, dark);

    ::selection {
      background-color: fade(@accent, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    #breadcrumbs,
    #printQuizButton,
    #content,
    .navigation-tray-container,
    .ic-Dashboard-header__layout,
    .button-sidebar-wide,
    .access_tokens,
    .context_modules,
    .textarea-container,
    .Button,
    .panel,
    span {
      background-color: @base;
      color: @accent;
    }

    #application,
    .comments,
    .module-sequence-footer-content,
    .immersive_reader_mount_point,
    .context_modules {
      background: @base !important;
      background-color: @base !important;
      color: @accent !important;
    }

    a,
    .page-title {
      color: fade(@text, 20%) !important;
    }

    * {
      color: @text !important;
      background-color: @base !important;
    }
    .ic-DashboardCard,
    .ic-DashboardCard__header_content,
    .ic-DashboardCard__action-container,
    .ic-DashboardCard__header-subtitle,
    .ic-DashboardCard__header-title,
    .ic-DashboardCard__header-term {
      background-color: @surface0 !important;
    }

    .ic-app-header__main-navigation,
    .ic-app-header__logomark-container {
      background-color: @crust !important;
    }

    .ic-app-header__menu-list-item.ic-app-header__menu-list-item--active
      .ic-app-header__menu-list-link {
      background-color: fade(@crust, 20%);
    }

    body {
      background-color: @base;
    }

    #right-side .events_list .event-details::after,
    #right-side .events_list .todo-details::after,
    #right-side .to-do-list .event-details::after,
    #right-side .to-do-list .todo-details::after {
      content: "";
      position: absolute;
      top: 0;
      right: 0;
      height: 100%;
      width: 12px;
      background: linear-gradient(
        to right,
        rgba(255, 255, 255, 0) 0%,
        @surface0 80%
      );
    }

    :root {
      --ic-brand-global-nav-avatar-border: @accent;
      --ic-brand-font-color-dark: fade(@accent, 20%);
      --ic-brand-primary: @accent;
      --ic-brand-global-nav-ic-icon-svg-fill: fade(@accent, 50%);
      --eMdva-color: @base !important;
      --ccWIh-color: @crust;
    }
    .unread_count,
    .menu-item-icon-container,
    .menu-item__badge,
    .nav-badge {
      color: fade(@text, 20%);
    }

    .list-view a.active {
      border-left-color: #007fff;
      color: #007fff;
    }

    // Fix file preview
    nav.ViewerControls * {
      background-color: @surface1 !important;
    }
    div.Pages,
    .Page-container {
      background-color: #f2f2f2 !important;
    }
    .Page-container * {
      background-color: transparent !important;
    }
  }
}

