    }

    [style="color:white;"], [style*="color:#ffffff"] {
      color: @text !important;
    }
    .mw-collapsible-toggle-default {
      &::before, &::after {
        color: @subtext0;
      }
    }

    a {
      &:focus {
        outline-color: @accent;
