    }

    & when not(@flavor = latte) {
      .nav {
        background: @mantle;
      }

      .external-link {
        background: @surface1;

        [stroke="#fff" i] {
          stroke: @text !important;
        }
      }

      .external-link .icon,
      .dropdown-menu:hover {
        --color-white: #rgbify(@crust)[];
      }

      [fill="#fff" i] {
        fill: @crust !important;
      }
    }

    & when (@flavor = latte) {
      --color-overlay: #rgbify(@text)[];

      .external-link {
        background: @subtext0;
      }

      .nav {
        background: @text;
      }

      a.title {
        --color-white: #rgbify(@base)[];
      }
    }
  }
